
var FS      = require('fs'),
    Path    = require('path'),
    INDEX_REGEX = /^index\.js$/;

//---------------------------------------------------------------------------
// @interface Minifier
// @implements Plugin
//---------------------------------------------------------------------------

FS.readdirSync(__dirname).forEach(function (name) {
    if (!INDEX_REGEX.test(name)) {
        require(Path.join(__dirname, name));
    }
});
